import React from 'react';
import './App.css'
class FirstPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,
      username: '',
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleEnter = this. handleEnter.bind(this);
  }

  componentDidMount(){

  }

  handleChange(e){
    this.setState({ [e.target.name]: e.target.value });
  }

  handleEnter(e){
    e.preventDefault();
  }

  render() {
    return (
      <div>
        <div>Welcome to</div>
        <div>The Best Group Chat Application</div>
        <div>Please Enter Your Username</div>
        <div className='form'>
          <form onSubmit={this.handleEnter}>
            <div> 
              <label className='username-label'>username :</label>
              <input className='username-input' type='text' name='username' onChange={this.onChange} required></input>
            </div>
            <div>
              <input className='enter-button' type='submit' value='Enter' />
            </div>
          </form>
        </div>
      </div>
    );
  }

}

export default FirstPage;
